public class loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int value = 0;
		// while loop intializing
		while (value < 6) {
			System.out.println("while loop value" + value);
			value += 1;
		}
		//for loop intialize
		for(int i=0;i<10 ;i++)
		{
			System.out.println("for loop value"+ i);
		}
	}

}
